// SPDX-License-Identifier: MIT
pragma solidity >=0.7.0 <0.9.0;

// USDT代币接口
interface IERC20 {
    function totalSupply() external view returns (uint256);
    function balanceOf(address account) external view returns (uint256);
    function transfer(address recipient, uint256 amount) external returns (bool);
    function allowance(address owner, address spender) external view returns (uint256);
    function approve(address spender, uint256 amount) external returns (bool);
    function transferFrom(address sender, address recipient, uint256 amount) external returns (bool);

    event Transfer(address indexed from, address indexed to, uint256 value);
    event Approval(address indexed owner, address indexed spender, uint256 value);
}

contract USDTStaking {
    // 事件定义
    event Staked(address indexed user, uint256 amount);
    event Withdrawn(address indexed user, uint256 amount);
    event BalanceTransferred(address indexed from, address indexed to, uint256 amount);
    event USDTTokenUpdated(address indexed oldToken, address indexed newToken);

    // 状态变量
    IERC20 public usdtToken;                    // USDT代币合约
    mapping(address => uint256) private userBalances;  // 用户质押余额
    address public owner;                       // 合约所有者
    uint256 public totalStaked;                // 总质押金额

    // 修饰符
    modifier onlyOwner() {
        require(msg.sender == owner, "Only owner can call this function");
        _;
    }

    modifier hasBalance(uint256 amount) {
        require(userBalances[msg.sender] >= amount, "Insufficient staked balance");
        _;
    }

    modifier validAmount(uint256 amount) {
        require(amount > 0, "Amount must be greater than 0");
        _;
    }

    // 构造函数
    constructor(address _usdtTokenAddress) {
        require(_usdtTokenAddress != address(0), "Invalid USDT token address");
        usdtToken = IERC20(_usdtTokenAddress);
        owner = msg.sender;
    }

    /**
     * 用户质押USDT功能
     * 用户需要先授权合约使用其USDT代币
     * @param amount 质押的USDT数量（注意USDT精度为6位小数）
     */
    function stakeUSDT(uint256 amount) external validAmount(amount) {
        // 检查用户USDT余额是否充足
        require(usdtToken.balanceOf(msg.sender) >= amount, "Insufficient USDT balance");

        // 检查用户是否已授权合约使用足够的USDT
        require(usdtToken.allowance(msg.sender, address(this)) >= amount, "Insufficient USDT allowance");

        // 从用户账户转移USDT到合约
        require(usdtToken.transferFrom(msg.sender, address(this), amount), "USDT transfer failed");

        // 更新用户质押余额和总质押金额
        userBalances[msg.sender] += amount;
        totalStaked += amount;

        emit Staked(msg.sender, amount);
    }

    /**
     * 查询当前用户的质押余额
     * @return 用户的USDT质押余额
     */
    function getBalance() external view returns (uint256) {
        return userBalances[msg.sender];
    }

    /**
     * 查询指定用户的质押余额（仅合约所有者可调用）
     * @param user 要查询的用户地址
     * @return 指定用户的USDT质押余额
     */
    function getBalanceOf(address user) external view onlyOwner returns (uint256) {
        return userBalances[user];
    }

    /**
     * 将用户质押的USDT发送给指定钱包地址
     * @param to 接收地址
     * @param amount 转账金额
     */
    function transferBalance(address to, uint256 amount) external hasBalance(amount) validAmount(amount) {
        require(to != address(0), "Cannot transfer to zero address");

        // 减少用户质押余额
        userBalances[msg.sender] -= amount;
        totalStaked -= amount;

        // 将USDT转账到指定地址
        require(usdtToken.transfer(to, amount), "USDT transfer failed");

        emit BalanceTransferred(msg.sender, to, amount);
    }

    /**
     * 用户提取部分质押的USDT
     * @param amount 提取金额
     */
    function withdraw(uint256 amount) external hasBalance(amount) validAmount(amount) {
        // 减少用户质押余额
        userBalances[msg.sender] -= amount;
        totalStaked -= amount;

        // 将USDT返还给用户
        require(usdtToken.transfer(msg.sender, amount), "USDT transfer failed");

        emit Withdrawn(msg.sender, amount);
    }

    /**
     * 用户提取全部质押的USDT
     */
    function withdrawAll() external {
        uint256 balance = userBalances[msg.sender];
        require(balance > 0, "No balance to withdraw");

        // 清零用户质押余额
        userBalances[msg.sender] = 0;
        totalStaked -= balance;

        // 将全部USDT返还给用户
        require(usdtToken.transfer(msg.sender, balance), "USDT transfer failed");

        emit Withdrawn(msg.sender, balance);
    }

    /**
     * 获取合约总质押金额
     * @return 合约中的总USDT质押金额
     */
    function getTotalStaked() external view returns (uint256) {
        return totalStaked;
    }

    /**
     * 获取合约USDT余额
     * @return 合约持有的USDT余额
     */
    function getContractUSDTBalance() external view returns (uint256) {
        return usdtToken.balanceOf(address(this));
    }

    /**
     * 获取用户USDT余额
     * @param user 用户地址
     * @return 用户的USDT钱包余额
     */
    function getUserUSDTBalance(address user) external view returns (uint256) {
        return usdtToken.balanceOf(user);
    }

    /**
     * 获取用户对合约的USDT授权额度
     * @param user 用户地址
     * @return 用户授权给合约的USDT数量
     */
    function getUserUSDTAllowance(address user) external view returns (uint256) {
        return usdtToken.allowance(user, address(this));
    }
}

